package assignment2;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=17;
		int b=14;
		int c=16;
		
		if (a>b&&a>c) {
			System.out.println("a is greater than b and c");
			
		}
		else if (b>a&&b>c) {
			System.out.println("b is greater than a and c");
			

		}
		else {
			System.out.println("c is greater than a and b");
		}

	
		
	}

}
